document.onscroll = function() {
    getProgressBar();
};

function getProgressBar() {
    const page = document.getElementById('about-service-full-2');

    var winScroll = page.body.scrollTop || page.documentElement.scrollTop;
    var height = page.documentElement.scrollHeight - page.documentElement.clientHeight;
    var scrolled = (winScroll / height) * 100;
    page.getElementById("progress-bar").style.width = scrolled + "%";
}
