const zoomOutFonts = () => {
    if (document.body.style.fontSize === "1.5em") {
        document.body.style.fontSize = "1.0em";
    }
};

const zoomUpFonts = () => {
    if (document.body.style.fontSize === "1.0em") {
        document.body.style.fontSize = "1.5em";
    }
};

export { zoomOutFonts, zoomUpFonts };
