import { changeTextsColor } from './change-texts-color';
import { generateBackgroundsColor } from './change-backgrounds-color';

let isNightMode = false;

const runNightMode = () => {
    if (isNightMode) {
        changeTextsColor();

        generateBackgroundsColor();
    }
};

export { runNightMode };
