const runFontChanging = () => {
    let h1 = document.querySelector('h1');
    let currentH1Size = h1.style.fontSize;
    alert(parseFloat(`${currentH1Size}`));
    // h1.setAttribute('style', 'font-size: ' + currentH1Size);
};

export { runFontChanging };
