import React from 'react';
import generateWeight from './generate-weight';

const recommendationPageTexts = document.querySelector('.recommendation-page').textContent;

window.onload = () => {
    const link = document.getElementById('link');
    let blob = new Blob([recommendationPageTexts],
        {type: 'text/plain'});

    link.href = URL.createObjectURL(blob);
};
