document.onscroll = function() {
    getProgressBar();
};

function getProgressBar() {
    const page = document.getElementById('about-service-full');

    var winScroll = window.body.scrollTop || window.documentElement.scrollTop;
    var height = window.documentElement.scrollHeight - window.documentElement.clientHeight;
    var scrolled = (winScroll / height) * 100;
    window.getElementById("progress-bar").style.width = scrolled + "%";
}
