// import { changeAllFonts } from './change-all-fonts';

const runFontChanging = () => {
    // changeAllFonts();

    let h1 = document.querySelector('h1');
    // let currentH1Size = window.getComputedStyle(h1);
    let currentH1Size = h1.style.fontSize;
    currentH1Size = '30px';
    // alert(h1.getAttribute('className'));
    // alert(h1.getAttribute('style'));
    h1.setAttribute('style', 'font-size: ' + currentH1Size);
};

export { runFontChanging };
