import { generateTextColor } from './generate-text-color';
// import './texts-color-details';

const changeTextsColor = (color) => {
    const generateLightTextInArray = () => {
        // const allNeededHeaders = ['.registration__h1'];
        const allDocumentTexts = ['p', 'h1', 'h2', 'h3', 'h5', 'thead', 'td', 'th', 'li', 'a', 'input', 'label'];

        for (let tag of allDocumentTexts) {
            generateTextColor(`${tag}`, `${color}`);
        }
    };

    generateLightTextInArray();
    // textColorDetails();
};

export { changeTextsColor };
