const generateFontZoom = (tagName) => {
    const currentTag = document.querySelectorAll(`${tagName}`);

    for (let tag of currentTag) {
        tag.style.fontSize = (currentSize + 1) + 'px';;
    }
};

export { generateFontZoom };
