// import { changeAllFonts } from './change-all-fonts';

const runFontChanging = () => {
    // changeAllFonts();

    const h1 = document.querySelector('h1');
    let currentH1Size = window.getComputedStyle(h1);
    currentH1Size.fontSize = 48 + 'px';
    alert(currentH1Size.fontSize);
};

export { runFontChanging };
