const zoomOutFonts = () => {
    if (document.body.style.fontSize === "1.064em") {
        document.body.style.fontSize = "1.0em";
    } else {
        document.body.style.fontSize = "1.064em";
    }
};

const zoomUpFonts = () => {
    document.body.style.fontSize = "1.1em";
    document.body.style.fontSize = "1.5em";
};

export { zoomOutFonts, zoomUpFonts };
