const generateTextColor = (tagName, color) => {
    const currentTag = document.querySelector(`${tagName}`);
    const currentTagAll = document.querySelectorAll(`${tagName}`);

    currentTag.style.color = color;

    for (let tag of currentTagAll) {
        tag.style.color = color;
    }
};

export { generateTextColor };
