const restoreFieldValues = () => {
    let email = document.querySelector('.login__email');

    email.setAttribute('value', '1');
    alert(email.value);
};

const saveInLocalStorage = () => {
    let email = document.querySelector('.login__email');

    localStorage.setItem('userData', email.value);
};

export { saveInLocalStorage, restoreFieldValues };
