import React from 'react';
import generateWeight from './generate-weight';

const recommendationPage = document.querySelector('.recommendation-page');
const recommendationPageText = recommendationPage.p.toString();

window.onload = () => {
    const link = document.getElementById('link');
    let blob = new Blob([recommendationPageText], {type: 'text/plain'});

    link.href = URL.createObjectURL(blob);
};
