import { generateFontZoom } from './generate-font-zoom';

const changeAllFonts = () => {
    const generateLightTextInArray = () => {
        const allDocumentFonts = ['p', 'h1', 'h2', 'h3', 'h5', 'thead', 'td', 'th', 'li', '.site-header a', 'input', 'label'];

        for (let tag of allDocumentFonts) {
            generateFontZoom(`${tag}`);
        }
    };

    generateLightTextInArray();
};

export { changeAllFonts };
