function validateLastName(e) {
    e.preventDefault();

    const _lastName = document.getElementById('last-name');

    if (_lastName !== null) {
        if (_lastName.value.length > 30) {
            alert('Фамилия должна быть не больше 30 символов.');
        }
    }
}

export default validateLastName;
