// import { changeAllFonts } from './change-all-fonts';

const runFontChanging = () => {
    // changeAllFonts();

    let h1 = document.querySelector('h1');
    // let currentH1Size = window.getComputedStyle(h1);
    const currentH1Size = h1.style.fontSize;
    alert(h1.getAttribute('style', 'fontSize'));
    // h1.setAttribute('style', `font-size: ${currentH1Size} * 2`);
};

export { runFontChanging };
